mapper.select:
/* common.getCodeGroupList */ SELECT code_type , code_id , code_nm , code_oder , use_yn FROM tb_code_info WHERE 1=1 AND code_id = '0000000000'
AND code_type = #{code_type}
AND use_yn = 'Y'

/* common.getCodeList */ SELECT code_type , code_id , code_nm , code_oder , use_yn FROM tb_code_info WHERE 1=1 AND code_type = #{code_type} AND code_id != '0000000000'
AND code_id = #{code_id}
AND use_yn = 'Y'

mapper.insert:
/* common.createCodeInfo */		
INSERT INTO tb_code_info (code_type, code_id, code_nm, code_oder)
VALUES (#{code_type}, #{code_id}, #{code_nm}, #{code_oder})

 		/* common.logInsert */	
		INSERT INTO tb_log_info (yyyymmdd, hhmmss, log_type, user_no, access_ip, etc01, etc02, etc03)
		VALUES(DATE_FORMAT(NOW(), '%Y%m%d'), DATE_FORMAT(NOW(), '%H%i%s'), #{log_type}, #{user_no}, #{access_ip}, #{etc01}, #{etc02}, #{etc03})
