mapper.select:

/* mylotto.getSaveNumListCnt */
SELECT 
	  COUNT(*)
FROM tb_my_save_num
WHERE 1=1
AND ex_count = #{ex_count}
AND user_no = #{user_no}


/* mylotto.getSaveNumNewListCnt */
SELECT 
	  COUNT(*)
FROM tb_my_save_num_new
WHERE 1=1
AND ex_count = #{ex_count}
AND user_no = #{user_no}

mapper.delete:

/* mylotto.deleteMyData */
DELETE FROM tb_my_save_num
WHERE EX_COUNT = #{ex_count}
AND USER_NO = #{user_no}

AND SEQ = #{seq}

/* mylotto.deleteMyDataNew */
DELETE FROM tb_my_save_num_new
WHERE EX_COUNT = #{ex_count}
AND USER_NO = #{user_no}

AND SEQ = #{seq}


/* mylotto.deleteMyNewMpngData */
DELETE FROM tb_expt_num_new_MPNG
WHERE EX_COUNT = #{ex_count}
AND USER_NO = #{user_no}

AND SEQ = #{seq}

mapper.insert:
/* mylotto.insertMyData */
INSERT INTO tb_my_save_num (EX_COUNT,USER_NO,SEQ,NUM1,NUM2,NUM3,NUM4,NUM5,NUM6,WIN_RSLT,EMAIL_SEND_YN,SMS_SEND_YN,CR_DT,UP_DT)
VALUES 

(#{item.ex_count},#{item.user_no},
 (
 	SELECT COUNT(*)+1 
	FROM tb_my_save_num 
	WHERE EX_COUNT = #{item.ex_count}
	AND USER_NO = #{item.user_no}
 ),
 #{item.num1},#{item.num2},#{item.num3},#{item.num4},#{item.num5},#{item.num6},NULL,'N','N',NOW(),NOW())

mapper.select:

/* mylotto.checkSaveMyData */
SELECT 
	  COUNT(*)
FROM tb_my_save_num
WHERE 1=1
AND ex_count = #{ex_count}
AND user_no = #{user_no}


/* mylotto.checkSaveMyDataNew */
SELECT 
	  COUNT(*)
FROM tb_my_save_num_new
WHERE 1=1
AND ex_count = #{ex_count}
AND user_no = #{user_no}


/* mylotto.getExptNumNewListCnt */
SELECT 
	COUNT(N.SEQ)
FROM tb_expt_num_new N
WHERE N.EX_COUNT = #{ex_count}

AND NOT EXISTS (
	SELECT
		SEQ
	FROM tb_expt_num_new_MPNG M
	WHERE EX_COUNT = #{ex_count}
	AND M.EX_COUNT = N.EX_COUNT
	AND M.SEQ = N.SEQ
)

/* mylotto.getExptNumNewList */ SELECT ex_count , seq , num1 , num2 , num3 , num4 , num5 , num6 , total , sum_end_num , low_high , odd_even , ac , ptrn_match_cnt , @rownum := @rownum+1 AS row_num FROM tb_expt_num_new N, (SELECT @rownum :=0) AS R WHERE ex_count = #{ex_count}
AND NOT EXISTS ( SELECT SEQ FROM tb_expt_num_new_MPNG M WHERE EX_COUNT = #{ex_count} AND M.EX_COUNT = N.EX_COUNT AND M.SEQ = N.SEQ )
ORDER BY seq
