mapper.select:
/* sysmng.getUserList */ SELECT * FROM ( SELECT user_no , email , nickname , grade , point , used_point , mbtlnum , thwd , ifnull(thwd_q,'') thwd_q , ifnull(thwd_a,'') thwd_a , ifnull(rcmd_user_no, 0) rcmd_user_no , svc_agree_yn , login_fail_cnt , auth_task , F_GETAUTHNM(auth_task) auth_task_nm , auth_menu , F_GETAUTHNM(auth_menu) auth_menu_nm , ifnull(use_yn, 'N') use_yn , CASE WHEN ifnull(use_yn, 'N') = 'Y' THEN '사용' ELSE '미사용' END use_yn_nm , cr_dt , up_dt , @rownum := @rownum+1 AS row_num FROM TB_USER_INFO, (SELECT @rownum :=0) AS R WHERE 1=1
AND email = #{search_key}
AND auth_task = #{auth_task}
ORDER BY
email
use_yn
email
auth_task
auth_menu
ASC
DESC
) AA
WHERE row_num BETWEEN #{startNum} AND #{endNum}


/* sysmng.getUserListCnt */
SELECT 
	  COUNT(*)
FROM TB_USER_INFO
WHERE 1=1
		
	AND email = #{search_key}
	
		
	AND auth_task = #{auth_task}

{CALL PW_USERINFO_I (
	#{reg_user_no}, #{access_ip}, 
	#{email}, #{nickname}, #{mbtlnum}, #{rcmd_user_no}, #{svc_agree_yn}
	)}

{CALL PW_USERINFO_U (
	#{reg_user_no}, #{access_ip}, 
	#{user_no}, #{nickname}, #{mbtlnum}, #{auth_task}, #{auth_menu}, #{use_yn}
	)}

/* sysmng.getWinDataList */ SELECT * FROM ( SELECT WIN_COUNT , NUM1 , NUM2 , NUM3 , NUM4 , NUM5 , NUM6 , BONUS_NUM , TOTAL , SUM_END_NUM , LOW_HIGH , ODD_EVEN , AC , DATE_FORMAT(CR_DT, '%Y%m%d%H%i%s') AS CR_DT , DATE_FORMAT(UP_DT, '%Y%m%d%H%i%s') AS UP_DT , @rownum := @rownum+1 AS row_num FROM TB_LOTTO_INFO, (SELECT @rownum :=0) AS R WHERE 1=1
AND WIN_COUNT = #{win_count}
ORDER BY
${sidx}
WIN_COUNT
${sord}
DESC
) AA
WHERE row_num BETWEEN #{startNum} AND #{endNum}

