/* sysmng.getWinDataAnlyList */ SELECT li.WIN_COUNT , li.NUM1 , li.NUM2 , li.NUM3 , li.NUM4 , li.NUM5 , li.NUM6 , li.BONUS_NUM , li.TOTAL , li.SUM_END_NUM , li.LOW_HIGH , li.ODD_EVEN , li.AC , DATE_FORMAT(li.CR_DT, '%Y%m%d%H%i%s') AS CR_DT , DATE_FORMAT(li.UP_DT, '%Y%m%d%H%i%s') AS UP_DT , csi.COUNT_SUM , csi.CONT_CNT , csi.NOT_CONT_CNT FROM TB_LOTTO_INFO li LEFT OUTER JOIN TB_COUNT_SUM_INFO csi ON csi.WIN_COUNT = li.WIN_COUNT WHERE 1=1
AND li.WIN_COUNT = #{win_count}
ORDER BY
${sidx}
li.WIN_COUNT
${sord}
DESC

