mapper.select:
/* sysmng.getAcGroupSumCnt */
SELECT IFNULL(SUM(CNT),0) CNT
FROM
(
	SELECT AC, COUNT(TOTAL) CNT
	FROM TB_LOTTO_INFO
	WHERE 1=1
	

	AND WIN_COUNT <= #{win_count}

	
	GROUP BY AC

	HAVING COUNT(AC) < #{cnt}

	ORDER BY AC
) A

/* sysmng.getAcGroupCntList */
SELECT AC, COUNT(AC) CNT
FROM TB_LOTTO_INFO
WHERE 1=1
	

	AND WIN_COUNT <= #{win_count}

	
GROUP BY AC

HAVING COUNT(AC) >= #{cnt}

ORDER BY AC

