mapper.select:
/* userAuth.getUserInfo */ SELECT user_no , email , nickname , grade , point , used_point , mbtlnum , thwd , rcmd_user_no , svc_agree_yn , login_fail_cnt , ifnull(use_yn, 'N') use_yn , auth_task , auth_menu , cr_dt , up_dt FROM tb_user_info WHERE 1=1
AND email = #{email}
AND user_no = #{user_no}

/* userAuth.getMenuAuthUrlList */ select tmi.menu_id , tmi.p_menu_id , tmi.menu_nm , CASE WHEN tmi.p_menu_id = 0 THEN 'P' ELSE 'C' END as m_type , tmi.menu_url , tmi.lna_element from tb_menu_info tmi
inner join ( select auth_cd, menu_id from tb_menu_authinfo where use_yn = 'Y' and auth_cd = ( SELECT auth_task FROM tb_user_info WHERE user_no = #{userNo} ) ) tmai ON tmai.menu_id = tmi.menu_id
where tmi.menu_id != 0 AND tmi.use_yn = 'Y' order by tmi.menu_id

/* userAuth.getMenuAuthUrlListForUser */ select tmi.menu_id , tmi.p_menu_id , tmi.menu_nm , CASE WHEN tmi.p_menu_id = 0 THEN 'P' ELSE 'C' END as m_type , tmi.menu_url , tmi.lna_element from tb_menu_info tmi where tmi.menu_id != 0 AND tmi.use_yn = 'Y'
AND tmi.common_yn = 'Y'
order by tmi.menu_id

SELECT COUNT(*)
FROM tb_user_info
WHERE email = #{email}

SELECT COUNT(*)
FROM tb_user_info
WHERE user_key = #{userKey}
